// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;
pragma experimental ABIEncoderV2;

import "./wfCashBase.sol";
import "@openzeppelin-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";

/// @dev This implementation contract is deployed as an UpgradeableBeacon. Each BeaconProxy
/// that uses this contract as an implementation will call initialize to set its own fCash id.
/// That identifier will represent the fCash that this ERC20 wrapper can hold.
abstract contract wfCashLogic is wfCashBase, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;
    // bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"))
    bytes4 internal constant ERC1155_ACCEPTED = 0xf23a6e61;

    constructor(INotionalV2 _notional, WETH9 _weth) wfCashBase(_notional, _weth) {}

    /***** Mint Methods *****/

    /// @notice Lends deposit amount in return for fCashAmount using underlying tokens
    /// @param depositAmountExternal amount of cash to deposit into this method
    /// @param fCashAmount amount of fCash to purchase (lend)
    /// @param receiver address to receive the fCash shares
    /// @param minImpliedRate minimum annualized interest rate to lend at
    function mintViaUnderlying(
        uint256 depositAmountExternal,
        uint88 fCashAmount,
        address receiver,
        uint32 minImpliedRate
    ) external override {
        (/* */, uint256 maxFCash) = getTotalFCashAvailable();
        _mintInternal(depositAmountExternal, fCashAmount, receiver, minImpliedRate, maxFCash);
    }

    function _mintInternal(
        uint256 depositAmountExternal,
        uint88 fCashAmount,
        address receiver,
        uint32 minImpliedRate,
        uint256 maxFCash
    ) internal nonReentrant {
        require(!hasMatured(), "fCash matured");
        (IERC20 token, bool isETH, bool hasTransferFee, uint256 precision) = _getTokenForMintInternal();
        uint256 balanceBefore = isETH ? WETH.balanceOf(address(this)) : token.balanceOf(address(this));
        uint256 msgValue;
        uint16 currencyId = getCurrencyId();

        if (isETH) {
            // Use WETH if lending ETH. Although Notional natively supports ETH, we use WETH here for integration
            // contracts so they only have to support ERC20 token transfers.
            // NOTE: safeTransferFrom not required since WETH is known to be compatible
            IERC20((address(WETH))).transferFrom(msg.sender, address(this), depositAmountExternal);
            WETH.withdraw(depositAmountExternal);
            msgValue = depositAmountExternal;
        } else {
            token.safeTransferFrom(msg.sender, address(this), depositAmountExternal);
            depositAmountExternal = token.balanceOf(address(this)) - balanceBefore;
        }

        if (maxFCash < fCashAmount) {
            // NOTE: lending at zero
            uint256 fCashAmountExternal = fCashAmount * precision / uint256(Constants.INTERNAL_TOKEN_PRECISION);
            require(fCashAmountExternal <= depositAmountExternal);

            // NOTE: Residual (depositAmountExternal - fCashAmountExternal) will be transferred
            // back to the account
            NotionalV2.depositUnderlyingToken{value: msgValue}(address(this), currencyId, fCashAmountExternal);
        } else if (isETH || hasTransferFee || getCashBalance() > 0) {
            _lendLegacy(currencyId, depositAmountExternal, fCashAmount, minImpliedRate, msgValue, isETH);
        } else {
            // Executes a lending action on Notional. Since this lending action uses an existing cash balance
            // prior to pulling payment, we cannot use it if there is a cash balance on the wrapper contract,
            // it will cause existing cash balances to be minted into fCash and create a shortfall. In normal
            // conditions, this method is more gas efficient.
            BatchLend[] memory action = EncodeDecode.encodeLendTrade(
                currencyId,
                getMarketIndex(),
                fCashAmount,
                minImpliedRate
            );
            NotionalV2.batchLend(address(this), action);
        }

        // Mints ERC20 tokens for the receiver
        _mint(receiver, fCashAmount);

        // Residual tokens will be sent back to msg.sender, not the receiver. The msg.sender
        // was used to transfer tokens in and these are any residual tokens left that were not
        // lent out. Sending these tokens back to the receiver risks them getting locked on a
        // contract that does not have the capability to transfer them off
        _sendTokensToReceiver(token, msg.sender, isETH, balanceBefore);
    }

    function _lendLegacy(
        uint16 currencyId,
        uint256 depositAmountExternal,
        uint88 fCashAmount,
        uint32 minImpliedRate,
        uint256 msgValue,
        bool isETH
    ) internal {
        // If dealing in ETH, we use WETH in the wrapper instead of ETH. NotionalV2 uses
        // ETH natively but due to pull payment requirements for batchLend, it does not support
        // ETH. batchLend only supports ERC20 tokens. Since the wrapper is a compatibility
        // layer, it will support WETH so integrators can deal solely in ERC20 tokens. Instead of using
        // "batchLend" we will use "batchBalanceActionWithTrades". The difference is that "batchLend"
        // is more gas efficient.

        // If deposit amount external is in excess of the cost to purchase fCash amount (often the case),
        // then we need to return the difference between postTradeCash - preTradeCash. This is done because
        // the encoded trade does not automatically withdraw the entire cash balance in case the wrapper
        // is holding a cash balance.
        uint256 preTradeCash = getCashBalance();

        BalanceActionWithTrades[] memory action = EncodeDecode.encodeLegacyLendTrade(
            currencyId,
            getMarketIndex(),
            depositAmountExternal,
            fCashAmount,
            minImpliedRate
        );
        // Notional will return any residual ETH as the native token. When we _sendTokensToReceiver those
        // native ETH tokens will be wrapped back to WETH.
        NotionalV2.batchBalanceAndTradeAction{value: msgValue}(address(this), action);

        uint256 postTradeCash = getCashBalance();

        if (preTradeCash != postTradeCash) {
            // If ETH, then redeem to WETH (redeemToUnderlying == false)
            NotionalV2.withdraw(currencyId, _safeUint88(postTradeCash - preTradeCash), !isETH);
        }
    }

    /// @notice This hook will be called every time this contract receives fCash, will validate that
    /// this is the correct fCash and then mint the corresponding amount of wrapped fCash tokens
    /// back to the user.
    function onERC1155Received(
        address /* _operator */,
        address _from,
        uint256 _id,
        uint256 _value,
        bytes calldata /* _data */
    ) external nonReentrant returns (bytes4) {
        uint256 fCashID = getfCashId();
        // Only accept erc1155 transfers from NotionalV2
        require(msg.sender == address(NotionalV2), "Invalid");
        // Only accept the fcash id that corresponds to the listed currency and maturity
        require(_id == fCashID, "Invalid");
        // Protect against signed value underflows
        require(int256(_value) > 0, "Invalid");

        // Double check the account's position, these are not strictly necessary and add gas costs
        // but might be good safe guards
        AccountContext memory ac = NotionalV2.getAccountContext(address(this));
        PortfolioAsset[] memory assets = NotionalV2.getAccountPortfolio(address(this));
        require(ac.hasDebt == 0x00);
        require(assets.length == 1);
        require(EncodeDecode.encodeERC1155Id(
                assets[0].currencyId,
                assets[0].maturity,
                assets[0].assetType) == fCashID
        );

        // Mint ERC20 tokens for the sender
        _mint(_from, _value);

        // This will allow the fCash to be accepted
        return ERC1155_ACCEPTED;
    }

    /***** Redeem (Burn) Methods *****/

    /// @notice Redeems tokens using custom options
    /// @dev re-entrancy is protected on _burn
    function redeem(uint256 amount, RedeemOpts memory opts) external override {
        _burnInternal(msg.sender, amount, opts);
    }

    /// @notice Redeems tokens to underlying
    /// @dev re-entrancy is protected on _burn
    function redeemToUnderlying(
        uint256 amount,
        address receiver,
        uint32 maxImpliedRate
    ) external override {
        _burnInternal(
            msg.sender,
            amount,
            RedeemOpts({
                redeemToUnderlying: true,
                transferfCash: false,
                receiver: receiver,
                maxImpliedRate: maxImpliedRate
            })
        );
    }

    /// @notice This method is here only in the case where someone has transferred invalid fCash
    /// to the contract and would prevent ERC1155 transfer hooks from succeeding. In this case the
    /// owner can recover the invalid fCash to a designated receiver. This can only occur if the fCash
    /// is transferred prior to contract creation.
    function recoverInvalidfCash(uint256 fCashId, address receiver) external {
        // Only the Notional owner can call this method
        require(msg.sender == NotionalV2.owner());
        // Cannot transfer the native fCash id of this wrapper
        require(fCashId != getfCashId());
        uint256 balance = NotionalV2.balanceOf(address(this), fCashId);
        // There should be a positive balance before we try to transfer this
        require(balance > 0);
        NotionalV2.safeTransferFrom(address(this), receiver, fCashId, balance, "");

        // Double check that we don't incur debt
        AccountContext memory ac = NotionalV2.getAccountContext(address(this));
        require(ac.hasDebt == 0x00);
    }

    /// @notice Called before tokens are burned (redemption) and so we will handle
    /// the fCash properly before and after maturity.
    function _burnInternal(
        address from,
        uint256 fCashShares,
        RedeemOpts memory opts
    ) internal nonReentrant {
        require(opts.receiver != address(0), "Receiver is zero address");
        require(opts.redeemToUnderlying || opts.transferfCash);
        // This will validate that the account has sufficient tokens to burn and make
        // any relevant underlying stateful changes to balances.
        super._burn(from, fCashShares);

        if (hasMatured()) {
            require(opts.transferfCash == false);
            // If the fCash has matured, then we need to ensure that the account is settled
            // and then we will transfer back the account's share of asset tokens.

            // This is a noop if the account is already settled, it is cheaper to call this method than
            // cache it in storage locally
            NotionalV2.settleAccount(address(this));
            uint16 currencyId = getCurrencyId();
            uint256 primeCashClaim = _getMaturedCashValue(fCashShares);

            // Transfer withdrawn tokens to the `from` address
            _withdrawCashToAccount(currencyId, opts.receiver, _safeUint88(primeCashClaim));
        } else if (opts.transferfCash) {
            // If the fCash has not matured, then we can transfer it via ERC1155.
            // NOTE: this may fail if the destination is a contract and it does not implement 
            // the `onERC1155Received` hook. If that is the case it is possible to use a regular
            // ERC20 transfer on this contract instead.
            NotionalV2.safeTransferFrom(
                address(this), // Sending from this contract
                opts.receiver, // Where to send the fCash
                getfCashId(), // fCash identifier
                fCashShares, // Amount of fCash to send
                ""
            );
        } else {
            _sellfCash(opts.receiver, fCashShares, opts.maxImpliedRate);
        }
    }

    /// @notice After maturity, withdraw cash back to account
    function _withdrawCashToAccount(
        uint16 currencyId,
        address receiver,
        uint88 primeCashToWithdraw
    ) private returns (uint256 tokensTransferred) {
        (IERC20 token, bool isETH) = getToken(true);
        uint256 balanceBefore = isETH ? WETH.balanceOf(address(this)) : token.balanceOf(address(this));

        NotionalV2.withdraw(currencyId, primeCashToWithdraw, !isETH);

        tokensTransferred = _sendTokensToReceiver(token, receiver, isETH, balanceBefore);
    }

    /// @dev Sells an fCash share back on the Notional AMM
    function _sellfCash(
        address receiver,
        uint256 fCashToSell,
        uint32 maxImpliedRate
    ) private returns (uint256 tokensTransferred) {
        (IERC20 token, bool isETH) = getToken(true);
        uint256 balanceBefore = isETH ? WETH.balanceOf(address(this)) : token.balanceOf(address(this));
        uint16 currencyId = getCurrencyId();

        (uint256 initialCashBalance, uint256 fCashBalance) = getBalances();
        bool hasInsufficientfCash = fCashBalance < fCashToSell;

        uint256 primeCashToWithdraw;
        if (hasInsufficientfCash) {
            // If there is insufficient fCash, calculate how much prime cash would be purchased if the
            // given fCash amount would be sold and that will be how much the wrapper will withdraw and
            // send to the receiver. Since fCash always sells at a discount to underlying prior to maturity,
            // the wrapper is guaranteed to have sufficient cash to send to the account.
            (/* */, primeCashToWithdraw, /* */, /* */) = NotionalV2.getPrincipalFromfCashBorrow(
                currencyId,
                fCashToSell,
                getMaturity(),
                0,
                block.timestamp
            );
            // If this is zero then it signifies that the trade will fail.
            require(primeCashToWithdraw > 0, "Redeem Failed");

            // Re-write the fCash to sell to the entire fCash balance.
            fCashToSell = fCashBalance;
        }

        if (fCashToSell > 0) {
            // Sells fCash on Notional AMM (via borrowing)
            BalanceActionWithTrades[] memory action = EncodeDecode.encodeBorrowTrade(
                currencyId,
                getMarketIndex(),
                _safeUint88(fCashToSell),
                maxImpliedRate
            );
            NotionalV2.batchBalanceAndTradeAction(address(this), action);
        }

        uint256 postTradeCash = getCashBalance();

        // If the account did not have insufficient fCash, then the amount of cash change here is what
        // the receiver is owed. In the other case, we transfer to the receiver the total calculated amount
        // above without modification.
        if (!hasInsufficientfCash) primeCashToWithdraw = postTradeCash - initialCashBalance;
        require(primeCashToWithdraw <= postTradeCash);

        // Withdraw the total amount of cash and send it to the receiver
        NotionalV2.withdraw(currencyId, _safeUint88(primeCashToWithdraw), !isETH);
        tokensTransferred = _sendTokensToReceiver(token, receiver, isETH, balanceBefore);
    }

    function _sendTokensToReceiver(
        IERC20 token,
        address receiver,
        bool isETH,
        uint256 balanceBefore
    ) private returns (uint256 tokensTransferred) {
        uint256 balanceAfter = isETH ? WETH.balanceOf(address(this)) : token.balanceOf(address(this));
        tokensTransferred = balanceAfter - balanceBefore;

        if (isETH) {
            // No need to use safeTransfer for WETH since it is known to be compatible
            IERC20(address(WETH)).transfer(receiver, tokensTransferred);
        } else if (tokensTransferred > 0) {
            token.safeTransfer(receiver, tokensTransferred);
        }
    }

    function _safeUint88(uint256 x) internal pure returns (uint88) {
        require(x <= uint256(type(uint88).max));
        return uint88(x);
    }
}

// SPDX-License-Identifier: BSUL-1.1
pragma solidity =0.7.6;
pragma abicoder v2;

import {
    Token,
    TokenType,
    TokenStorage,
    PrimeRate
} from "../../global/Types.sol";
import {LibStorage} from "../../global/LibStorage.sol";
import {SafeInt256} from "../../math/SafeInt256.sol";
import {SafeUint256} from "../../math/SafeUint256.sol";
import {Constants} from "../../global/Constants.sol";
import {Deployments} from "../../global/Deployments.sol";

import {Emitter} from "../Emitter.sol";
import {PrimeCashExchangeRate} from "../pCash/PrimeCashExchangeRate.sol";
import {PrimeRateLib} from "../pCash/PrimeRateLib.sol";

import {ExternalLending} from "./ExternalLending.sol";
import {CompoundHandler} from "./protocols/CompoundHandler.sol";
import {GenericToken} from "./protocols/GenericToken.sol";

import {IERC20} from "../../../interfaces/IERC20.sol";

/// @notice Handles all external token transfers and events
library TokenHandler {
    using SafeInt256 for int256;
    using SafeUint256 for uint256;
    using PrimeRateLib for PrimeRate;

    function getDeprecatedAssetToken(uint256 currencyId) internal view returns (Token memory) {
        return _getToken(currencyId, false);
    }

    function getUnderlyingToken(uint256 currencyId) internal view returns (Token memory) {
        return _getToken(currencyId, true);
    }

    /// @notice Gets token data for a particular currency id, if underlying is set to true then returns
    /// the underlying token. (These may not always exist)
    function _getToken(uint256 currencyId, bool underlying) private view returns (Token memory) {
        mapping(uint256 => mapping(bool => TokenStorage)) storage store = LibStorage.getTokenStorage();
        TokenStorage storage tokenStorage = store[currencyId][underlying];

        return
            Token({
                tokenAddress: tokenStorage.tokenAddress,
                hasTransferFee: tokenStorage.hasTransferFee,
                // No overflow, restricted on storage
                decimals: int256(10**tokenStorage.decimalPlaces),
                tokenType: tokenStorage.tokenType,
                deprecated_maxCollateralBalance: 0
            });
    }

    /// @notice Sets a token for a currency id. After the prime cash migration, only
    /// underlying tokens may be set by this method.
    function setToken(uint256 currencyId, TokenStorage memory tokenStorage) internal {
        mapping(uint256 => mapping(bool => TokenStorage)) storage store = LibStorage.getTokenStorage();

        if (tokenStorage.tokenType == TokenType.Ether && currencyId == Constants.ETH_CURRENCY_ID) {
            // Hardcoded parameters for ETH just to make sure we don't get it wrong.
            TokenStorage storage ts = store[currencyId][true];
            ts.tokenAddress = address(0);
            ts.hasTransferFee = false;
            ts.tokenType = TokenType.Ether;
            ts.decimalPlaces = Constants.ETH_DECIMAL_PLACES;

            return;
        }

        // Check token address
        require(tokenStorage.tokenAddress != address(0));
        // Once a token is set we cannot override it. In the case that we do need to do change a token address
        // then we should explicitly upgrade this method to allow for a token to be changed.
        Token memory token = _getToken(currencyId, true);
        require(token.tokenAddress == tokenStorage.tokenAddress || token.tokenAddress == address(0));

        require(0 < tokenStorage.decimalPlaces && tokenStorage.decimalPlaces <= Constants.MAX_DECIMAL_PLACES);

        // Validate token type
        require(tokenStorage.tokenType != TokenType.Ether); // dev: ether can only be set once
        // Only underlying tokens allowed after migration
        require(tokenStorage.tokenType == TokenType.UnderlyingToken); // dev: only underlying token

        // Underlying is always true.
        store[currencyId][true] = tokenStorage;
    }

    /**
     * @notice Transfers a deprecated asset token into Notional and redeems it for underlying,
     * updates prime cash supply and returns the total prime cash to add to the account.
     * @param assetToken asset token to redeem
     * @param currencyId the currency id of the token
     * @param assetAmountExternal the amount to transfer in asset token denomination and external precision
     * @param primeRate the prime rate for the given currency
     * @param account the address of the account to transfer from
     * @return primeCashDeposited the amount of prime cash to mint back to the account
     */
    function depositDeprecatedAssetToken(
        Token memory assetToken,
        uint16 currencyId,
        uint256 assetAmountExternal,
        address account,
        PrimeRate memory primeRate
    ) internal returns (int256 primeCashDeposited) {
        // Transfer the asset token into the contract
        assetAmountExternal = GenericToken.safeTransferIn(
            assetToken.tokenAddress, account, assetAmountExternal
        );

        Token memory underlyingToken = getUnderlyingToken(currencyId);
        int256 underlyingExternalAmount;
        // Only cTokens will be listed at the time of the migration. Redeem
        // those cTokens to underlying (to be held by the Notional contract)
        // and then run the post transfer update
        if (assetToken.tokenType == TokenType.cETH) {
            underlyingExternalAmount = CompoundHandler.redeemCETH(
                assetToken, assetAmountExternal
            ).toInt();
        } else if (assetToken.tokenType == TokenType.cToken) {
            underlyingExternalAmount = CompoundHandler.redeem(
                assetToken, underlyingToken, assetAmountExternal
            ).toInt();
        } else {
            // No other asset token variants can be called here.
            revert();
        }

        primeCashDeposited = _postTransferPrimeCashUpdate(
            account, currencyId, underlyingExternalAmount, underlyingToken, primeRate
        );
    }

    /// @notice Deposits an exact amount of underlying tokens to mint the specified amount of prime cash.
    /// @param account account to transfer tokens from
    /// @param currencyId the associated currency id
    /// @param primeCashToMint the amount of prime cash to mint
    /// @param primeRate the current accrued prime rate
    /// @param returnNativeTokenWrapped if true, return excess msg.value ETH payments as WETH
    /// @return actualTransferExternal the actual amount of tokens transferred in external precision
    function depositExactToMintPrimeCash(
        address account,
        uint16 currencyId,
        int256 primeCashToMint,
        PrimeRate memory primeRate,
        bool returnNativeTokenWrapped
    ) internal returns (int256 actualTransferExternal) {
        if (primeCashToMint == 0) return 0;
        require(primeCashToMint > 0);
        Token memory underlying = getUnderlyingToken(currencyId);
        int256 netTransferExternal = convertToUnderlyingExternalWithAdjustment(
            underlying, 
            primeRate.convertToUnderlying(primeCashToMint) 
        );

        int256 netPrimeSupplyChange;
        (actualTransferExternal, netPrimeSupplyChange) = depositUnderlyingExternal(
            account, currencyId, netTransferExternal, primeRate, returnNativeTokenWrapped
        );

        // Ensures that the prime cash minted is positive and always greater than
        // the amount of prime cash that will be credited to the depositor. Any dust
        // amounts here will accrue to the protocol. primeCashToMint is asserted to be
        // positive so if netPrimeSupplyChange is negative (which it should never be),
        // then this will revert as well.
        int256 diff = netPrimeSupplyChange - primeCashToMint;
        require(0 <= diff); // dev: diff above zero
    }

    /// @notice Deposits an amount of underlying tokens to mint prime cash
    /// @param account account to transfer tokens from
    /// @param currencyId the associated currency id
    /// @param _underlyingExternalDeposit the amount of underlying tokens to deposit
    /// @param primeRate the current accrued prime rate
    /// @param returnNativeTokenWrapped if true, return excess msg.value ETH payments as WETH
    /// @return actualTransferExternal the actual amount of tokens transferred in external precision
    /// @return netPrimeSupplyChange the amount of prime supply created
    function depositUnderlyingExternal(
        address account,
        uint16 currencyId,
        int256 _underlyingExternalDeposit,
        PrimeRate memory primeRate,
        bool returnNativeTokenWrapped
    ) internal returns (int256 actualTransferExternal, int256 netPrimeSupplyChange) {
        uint256 underlyingExternalDeposit = _underlyingExternalDeposit.toUint();
        if (underlyingExternalDeposit == 0) return (0, 0);

        Token memory underlying = getUnderlyingToken(currencyId);
        if (underlying.tokenType == TokenType.Ether) {
            // Underflow checked above
            if (underlyingExternalDeposit < msg.value) {
                // Transfer any excess ETH back to the account
                GenericToken.transferNativeTokenOut(
                    account, msg.value - underlyingExternalDeposit, returnNativeTokenWrapped
                );
            } else {
                require(underlyingExternalDeposit == msg.value, "ETH Balance");
            }

            actualTransferExternal = _underlyingExternalDeposit;
        } else {
            // In the case of deposits, we use a balance before and after check
            // to ensure that we record the proper balance change.
            actualTransferExternal = GenericToken.safeTransferIn(
                underlying.tokenAddress, account, underlyingExternalDeposit
            ).toInt();
        }

        netPrimeSupplyChange = _postTransferPrimeCashUpdate(
            account, currencyId, actualTransferExternal, underlying, primeRate
        );
    }

    /// @notice Withdraws an amount of prime cash and returns it to the account as underlying tokens
    /// @param account account to transfer tokens to
    /// @param currencyId the associated currency id
    /// @param primeCashToWithdraw the amount of prime cash to burn
    /// @param primeRate the current accrued prime rate
    /// @param withdrawWrappedNativeToken if true, return ETH as WETH
    /// @return netTransferExternal the amount of underlying tokens withdrawn in native precision, this is
    /// negative to signify that tokens have left the protocol
    function withdrawPrimeCash(
        address account,
        address receiver,
        uint16 currencyId,
        int256 primeCashToWithdraw,
        PrimeRate memory primeRate,
        bool withdrawWrappedNativeToken
    ) internal returns (int256 netTransferExternal) {
        if (primeCashToWithdraw == 0) return 0;
        require(primeCashToWithdraw < 0);

        Token memory underlying = getUnderlyingToken(currencyId);
        netTransferExternal = convertToExternal(
            underlying, 
            primeRate.convertToUnderlying(primeCashToWithdraw) 
        );

        // Overflow not possible due to int256
        uint256 withdrawAmount = uint256(netTransferExternal.neg());
        ExternalLending.redeemMoneyMarketIfRequired(currencyId, underlying, withdrawAmount);

        if (underlying.tokenType == TokenType.Ether) {
            GenericToken.transferNativeTokenOut(receiver, withdrawAmount, withdrawWrappedNativeToken);
        } else {
            GenericToken.safeTransferOut(underlying.tokenAddress, receiver, withdrawAmount);
        }

        _postTransferPrimeCashUpdate(account, currencyId, netTransferExternal, underlying, primeRate);
    }

    /// @notice Every time tokens are transferred into or out of the protocol, the prime supply
    /// and total underlying held must be updated.
    function _postTransferPrimeCashUpdate(
        address account,
        uint16 currencyId,
        int256 netTransferUnderlyingExternal,
        Token memory underlyingToken,
        PrimeRate memory primeRate
    ) private returns (int256 netPrimeSupplyChange) {
        int256 netUnderlyingChange = convertToInternal(underlyingToken, netTransferUnderlyingExternal);

        netPrimeSupplyChange = primeRate.convertFromUnderlying(netUnderlyingChange);

        Emitter.emitMintOrBurnPrimeCash(account, currencyId, netPrimeSupplyChange);
        PrimeCashExchangeRate.updateTotalPrimeSupply(currencyId, netPrimeSupplyChange, netUnderlyingChange);

        _updateNetStoredTokenBalance(underlyingToken.tokenAddress, netTransferUnderlyingExternal);
    }

    function convertToInternal(Token memory token, int256 amount) internal pure returns (int256) {
        // If token decimals > INTERNAL_TOKEN_PRECISION:
        //  on deposit: resulting dust will accumulate to protocol
        //  on withdraw: protocol may lose dust amount. However, withdraws are only calculated based
        //    on a conversion from internal token precision to external token precision so therefore dust
        //    amounts cannot be specified for withdraws.
        // If token decimals < INTERNAL_TOKEN_PRECISION then this will add zeros to the
        // end of amount and will not result in dust.
        if (token.decimals == Constants.INTERNAL_TOKEN_PRECISION) return amount;
        return amount.mul(Constants.INTERNAL_TOKEN_PRECISION).div(token.decimals);
    }

    function convertToExternal(Token memory token, int256 amount) internal pure returns (int256) {
        if (token.decimals == Constants.INTERNAL_TOKEN_PRECISION) return amount;
        // If token decimals > INTERNAL_TOKEN_PRECISION then this will increase amount
        // by adding a number of zeros to the end and will not result in dust.
        // If token decimals < INTERNAL_TOKEN_PRECISION:
        //  on deposit: Deposits are specified in external token precision and there is no loss of precision when
        //      tokens are converted from external to internal precision
        //  on withdraw: this calculation will round down such that the protocol retains the residual cash balance
        return amount.mul(token.decimals).div(Constants.INTERNAL_TOKEN_PRECISION);
    }

    /// @notice Converts a token to an underlying external amount with adjustments for rounding errors when depositing
    function convertToUnderlyingExternalWithAdjustment(
        Token memory token,
        int256 underlyingInternalAmount
    ) internal pure returns (int256 underlyingExternalAmount) {
        if (token.decimals < Constants.INTERNAL_TOKEN_PRECISION) {
            // If external < 8, we could truncate down and cause an off by one error, for example we need
            // 1.00000011 cash and we deposit only 1.000000, missing 11 units. Therefore, we add a unit at the
            // lower precision (external) to get around off by one errors
            underlyingExternalAmount = convertToExternal(token, underlyingInternalAmount).add(1);
        } else {
            // If external > 8, we may not mint enough asset tokens because in the case of 1e18 precision 
            // an off by 1 error at 1e8 precision is 1e10 units of the underlying token. In this case we
            // add 1 at the internal precision which has the effect of rounding up by 1e10
            underlyingExternalAmount = convertToExternal(token, underlyingInternalAmount.add(1));
        }
    }

    /// @notice Convenience method for getting the balance using a token object
    function balanceOf(Token memory token, address account) internal view returns (uint256) {
        if (token.tokenType == TokenType.Ether) {
            return account.balance;
        } else {
            return IERC20(token.tokenAddress).balanceOf(account);
        }
    }

    function transferIncentive(address account, uint256 tokensToTransfer) internal {
        GenericToken.safeTransferOut(Deployments.NOTE_TOKEN_ADDRESS, account, tokensToTransfer);
    }

