    error ProxyFactory__ContestIsNotRegistered();
    error ProxyFactory__ContestIsNotExpired();
    error ProxyFactory__DelegateCallFailed();
    error ProxyFactory__ProxyAddressCannotBeZero();

    /////////////////////
    /////// Event ///////
    /////////////////////
