        for (uint256 i; i < _whitelistedTokens.length;) {
            if (_whitelistedTokens[i] == address(0)) revert ProxyFactory__NoZeroAddress();
            whitelistedTokens[_whitelistedTokens[i]] = true;
            unchecked {
                i++;
            }
        }
    }

    ////////////////////////////////////////////
