// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { ERC721Holder } from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import { SafeMath } from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import { TransferHelper } from "../libraries/TransferHelper.sol";
import { IRdpxV2Core } from "../core/IRdpxV2Core.sol";

// Uniswamp V3
import "../uniswap_V3/IUniswapV3Factory.sol";
import "../uniswap_V3/libraries/TickMath.sol";
import "../uniswap_V3/libraries/LiquidityAmounts.sol";
import "../uniswap_V3/periphery/interfaces/INonfungiblePositionManager.sol";
import "../uniswap_V3/IUniswapV3Pool.sol";
import "../uniswap_V3/ISwapRouter.sol";

// Interfaces
import { IERC20WithBurn } from "../interfaces/IERC20WithBurn.sol";

abstract contract OracleLike {
  function read() external view virtual returns (uint);

  function uniswapPool() external view virtual returns (address);
}

contract UniV3LiquidityAMO is AccessControl, ERC721Holder {
  using SafeMath for uint256;
  using SafeERC20 for IERC20WithBurn;

  /* ========== STATE VARIABLES ========== */

  // Uniswap v3
  IUniswapV3Factory public univ3_factory;
  INonfungiblePositionManager public univ3_positions;
  ISwapRouter public univ3_router;

  // Details about the AMO's uniswap positions
  struct Position {
    uint256 token_id;
    address collateral_address;
    uint128 liquidity; // the liquidity of the position
    int24 tickLower; // the tick range of the position
    int24 tickUpper;
    uint24 fee_tier;
  }

  // Add liquidity param
  struct AddLiquidityParams {
    address _tokenA;
    address _tokenB;
    int24 _tickLower;
    int24 _tickUpper;
    uint24 _fee;
    uint256 _amount0Desired;
    uint256 _amount1Desired;
    uint256 _amount0Min;
    uint256 _amount1Min;
  }

  // Array of all Uni v3 NFT positions held by the AMO
  Position[] public positions_array;

  // Map token_id to Position
  mapping(uint256 => Position) public positions_mapping;

  // Rdpx address
  address public rdpx;

  // RdpxV2Core address
  address public rdpxV2Core;

  /* ========== CONSTRUCTOR ========== */

  constructor(address _rdpx, address _rdpxV2Core) {
    rdpx = _rdpx;
    rdpxV2Core = _rdpxV2Core;

    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

    univ3_factory = IUniswapV3Factory(
      0x1F98431c8aD98523631AE4a59f267346ea31F984
    );
    univ3_positions = INonfungiblePositionManager(
      0xC36442b4a4522E871399CD717aBDD847Ab11FE88
    );
    univ3_router = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
  }

  /* ========== VIEWS ========== */

  // Returns this contract's liquidity in a specific [Rdpx]-[collateral] uni v3 pool
  function liquidityInPool(
    address _collateral_address,
    int24 _tickLower,
    int24 _tickUpper,
    uint24 _fee
  ) public view returns (uint128) {
    IUniswapV3Pool get_pool = IUniswapV3Pool(
      univ3_factory.getPool(address(rdpx), _collateral_address, _fee)
    );

    // goes into the pool's positions mapping, and grabs this address's liquidity
    (uint128 liquidity, , , , ) = get_pool.positions(
      keccak256(abi.encodePacked(address(this), _tickLower, _tickUpper))
    );
    return liquidity;
  }

  // Only counts non-withdrawn positions
  function numPositions() public view returns (uint256) {
    return positions_array.length;
  }

  /* ========== RESTRICTED FUNCTIONS, BUT CUSTODIAN CAN CALL ========== */

  // Iterate through all positions and collect fees accumulated
  function collectFees() external onlyRole(DEFAULT_ADMIN_ROLE) {
    for (uint i = 0; i < positions_array.length; i++) {
      Position memory current_position = positions_array[i];
      INonfungiblePositionManager.CollectParams
        memory collect_params = INonfungiblePositionManager.CollectParams(
          current_position.token_id,
          rdpxV2Core,
          type(uint128).max,
          type(uint128).max
        );

      // Send to custodian address
      univ3_positions.collect(collect_params);
    }
  }

  /* ---------------------------------------------------- */
  /* ---------------------- Uni v3 ---------------------- */
  /* ---------------------------------------------------- */

  function approveTarget(
    address _target,
    address _token,
    uint256 _amount,
    bool use_safe_approve
  ) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (use_safe_approve) {
      // safeApprove needed for USDT and others for the first approval
      // You need to approve 0 every time beforehand for USDT: it resets
      TransferHelper.safeApprove(_token, _target, _amount);
    } else {
      IERC20WithBurn(_token).approve(_target, _amount);
    }
  }

  // IUniswapV3Pool public current_uni_pool; // only used for mint callback; is set and accessed during execution of addLiquidity()
  function addLiquidity(
    AddLiquidityParams memory params
  ) public onlyRole(DEFAULT_ADMIN_ROLE) {
    IERC20WithBurn(params._tokenA).transferFrom(
      rdpxV2Core,
      address(this),
      params._amount0Desired
    );
    IERC20WithBurn(params._tokenB).transferFrom(
      rdpxV2Core,
      address(this),
      params._amount1Desired
    );

    IERC20WithBurn(params._tokenA).approve(
      address(univ3_positions),
      params._amount0Desired
    );
    IERC20WithBurn(params._tokenB).approve(
      address(univ3_positions),
      params._amount1Desired
    );

    INonfungiblePositionManager.MintParams
      memory mintParams = INonfungiblePositionManager.MintParams(
        params._tokenA,
        params._tokenB,
        params._fee,
        params._tickLower,
        params._tickUpper,
        params._amount0Desired,
        params._amount1Desired,
        params._amount0Min,
        params._amount1Min,
        address(this),
        type(uint256).max
      );

    (uint256 tokenId, uint128 amountLiquidity, , ) = univ3_positions.mint(
      mintParams
    );

    Position memory pos = Position(
      tokenId,
      params._tokenA == address(rdpx) ? params._tokenB : params._tokenA,
      amountLiquidity,
      params._tickLower,
      params._tickUpper,
      params._fee
    );

    positions_array.push(pos);
    positions_mapping[tokenId] = pos;

    // send tokens to rdpxV2Core
    _sendTokensToRdpxV2Core(params._tokenA, params._tokenB);
  }

  function removeLiquidity(
    uint256 positionIndex,
    uint256 minAmount0,
    uint256 minAmount1
  ) public onlyRole(DEFAULT_ADMIN_ROLE) {
    Position memory pos = positions_array[positionIndex];
    INonfungiblePositionManager.CollectParams
      memory collect_params = INonfungiblePositionManager.CollectParams(
        pos.token_id,
        rdpxV2Core,
        type(uint128).max,
        type(uint128).max
      );

    (
      ,
      ,
      address tokenA,
      address tokenB,
      ,
      ,
      ,
      uint128 liquidity,
      ,
      ,
      ,

    ) = univ3_positions.positions(pos.token_id);

    // remove liquidity
    INonfungiblePositionManager.DecreaseLiquidityParams
      memory decreaseLiquidityParams = INonfungiblePositionManager
        .DecreaseLiquidityParams(
          pos.token_id,
          liquidity,
          minAmount0,
          minAmount1,
          block.timestamp
        );

    univ3_positions.decreaseLiquidity(decreaseLiquidityParams);

    univ3_positions.collect(collect_params);

    univ3_positions.burn(pos.token_id);

    positions_array[positionIndex] = positions_array[
      positions_array.length - 1
    ];
    positions_array.pop();
    delete positions_mapping[pos.token_id];

    // send tokens to rdpxV2Core
    _sendTokensToRdpxV2Core(tokenA, tokenB);

    emit log(positions_array.length);
    emit log(positions_mapping[pos.token_id].token_id);
  }

  // Swap tokenA into tokenB using univ3_router.ExactInputSingle()
  // Uni V3 only
  function swap(
    address _tokenA,
    address _tokenB,
    uint24 _fee_tier,
    uint256 _amountAtoB,
    uint256 _amountOutMinimum,
    uint160 _sqrtPriceLimitX96
  ) public onlyRole(DEFAULT_ADMIN_ROLE) returns (uint256) {
    // transfer token from rdpx v2 core
    IERC20WithBurn(_tokenA).transferFrom(
      rdpxV2Core,
      address(this),
      _amountAtoB
    );

    ISwapRouter.ExactInputSingleParams memory swap_params = ISwapRouter
      .ExactInputSingleParams(
        _tokenA,
        _tokenB,
        _fee_tier,
        address(this),
        2105300114, // Expiration: a long time from now
        _amountAtoB,
        _amountOutMinimum,
        _sqrtPriceLimitX96
      );

    // Approval
    TransferHelper.safeApprove(_tokenA, address(univ3_router), _amountAtoB);

    uint256 amountOut = univ3_router.exactInputSingle(swap_params);

    _sendTokensToRdpxV2Core(_tokenA, _tokenB);
    return amountOut;
  }

  /* ========== OWNER / GOVERNANCE FUNCTIONS ONLY ========== */
  // Only owner or timelock can call, to limit risk

  function recoverERC20(
    address tokenAddress,
    uint256 tokenAmount
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    // Can only be triggered by owner or governance, not custodian
    // Tokens are sent to the custodian, as a sort of safeguard
    TransferHelper.safeTransfer(tokenAddress, rdpxV2Core, tokenAmount);

    emit RecoveredERC20(tokenAddress, tokenAmount);
  }

  function recoverERC721(
    address tokenAddress,
    uint256 token_id
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    // Only the owner address can ever receive the recovery withdrawal
    // INonfungiblePositionManager inherits IERC721 so the latter does not need to be imported
    INonfungiblePositionManager(tokenAddress).safeTransferFrom(
      address(this),
      rdpxV2Core,
      token_id
    );
    emit RecoveredERC721(tokenAddress, token_id);
  }

  // Generic proxy
  function execute(
    address _to,
    uint256 _value,
    bytes calldata _data
  ) external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool, bytes memory) {
    (bool success, bytes memory result) = _to.call{ value: _value }(_data);
    return (success, result);
  }

  /* ========== INTERNAL ========== */

  /**
   * @dev sends token A and B to the rdpxV2Core
   */
  function _sendTokensToRdpxV2Core(address tokenA, address tokenB) internal {
    uint256 tokenABalance = IERC20WithBurn(tokenA).balanceOf(address(this));
    uint256 tokenBBalance = IERC20WithBurn(tokenB).balanceOf(address(this));
    // transfer token A and B from this contract to the rdpxV2Core
    IERC20WithBurn(tokenA).safeTransfer(rdpxV2Core, tokenABalance);
    IERC20WithBurn(tokenB).safeTransfer(rdpxV2Core, tokenBBalance);

    // sync token balances
    IRdpxV2Core(rdpxV2Core).sync();

    emit LogAssetsTransfered(tokenABalance, tokenBBalance, tokenA, tokenB);
  }

  /* ========== EVENTS ========== */

  event RecoveredERC20(address token, uint256 amount);
  event RecoveredERC721(address token, uint256 id);
  event LogAssetsTransfered(
    uint256 tokenAAmount,
    uint256 tokenBAmount,
    address tokenAAddress,
    address tokenBAddress
  );
  /*
   **  burn tokenAmount from the recipient and send tokens to the receipient
   */
  event log(uint);
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

// Contracts
import { ERC20 } from "solmate/src/tokens/ERC20.sol";
import { FixedPointMathLib } from "solmate/src/utils/FixedPointMathLib.sol";
import { IERC20WithBurn } from "../interfaces/IERC20WithBurn.sol";

// Libraries
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SafeTransferLib } from "solmate/src/utils/SafeTransferLib.sol";

// Interfaces
import { IPerpetualAtlanticVault } from "./IPerpetualAtlanticVault.sol";
import { IPerpetualAtlanticVaultLP } from "./IPerpetualAtlanticVaultLP.sol";

/**
 * @title PerpetaulAtlanticVault LP Token
 */
contract PerpetualAtlanticVaultLP is ERC20, IPerpetualAtlanticVaultLP {
  using SafeERC20 for IERC20WithBurn;
  using SafeTransferLib for ERC20;
  using FixedPointMathLib for uint256;

  // ================================ EVENTS ================================ //

  event Deposit(
    address indexed caller,
    address indexed owner,
    uint256 assets,
    uint256 shares
  );

  event Withdraw(
    address indexed caller,
    address indexed receiver,
    address indexed owner,
    uint256 assets,
    uint256 shares
  );

  // ================================ STATE VARIABLES ================================ //

  /// @dev The address of the perpetual Atlantic Vault contract creating the lp token
  IPerpetualAtlanticVault public perpetualAtlanticVault;

  /// @dev The collateral token
  ERC20 public collateral;

  /// @dev The symbol reperesenting the underlying asset of the perpetualatlanticvault lp
  string public underlyingSymbol;

  /// @dev The symbol representing the collateral token of the perpetualatlanticvault lp
  string public collateralSymbol;

  /// @dev Total collateral available
  uint256 private _totalCollateral;

  /// @dev Active collateral
  uint256 private _activeCollateral;

  /// @dev Total rdpx available
  uint256 private _rdpxCollateral;

  /// @dev address of rdpx token
  address public rdpx;

  /// @dev address of the rdpx rdpxV2Core contract
  address public rdpxRdpxV2Core;

  // ================================ CONSTRUCTOR ================================ //

  /**
   * @param _perpetualAtlanticVault The address of the perpetual atlantic vault contract creating the lp token
   * @param _rdpxRdpxV2Core The address of the rdpx rdpxV2Core contract
   * @param _collateral The address of the collateral asset in the perpetualatlanticvault contract
   * @param _collateralSymbol The symbol of the collateral asset token
   * @param _rdpx The address of the rdpx token
   */
  constructor(
    address _perpetualAtlanticVault,
    address _rdpxRdpxV2Core,
    address _collateral,
    address _rdpx,
    string memory _collateralSymbol
  )
    ERC20(
      "PerpetualAtlanticVault LP Token",
      _collateralSymbol,
      ERC20(_collateral).decimals()
    )
  {
    require(
      _perpetualAtlanticVault != address(0) || _rdpx != address(0),
      "ZERO_ADDRESS"
    );
    perpetualAtlanticVault = IPerpetualAtlanticVault(_perpetualAtlanticVault);
    rdpxRdpxV2Core = _rdpxRdpxV2Core;
    collateralSymbol = _collateralSymbol;
    rdpx = _rdpx;
    collateral = ERC20(_collateral);

    symbol = string.concat(_collateralSymbol, "-LP");

    collateral.approve(_perpetualAtlanticVault, type(uint256).max);
    ERC20(rdpx).approve(_perpetualAtlanticVault, type(uint256).max);
  }

  // ================================ PUBLIC FUNCTIONS ================================ //

  /**
   * @notice deposit into ERC4626 token
   * @param assets assets
   * @param receiver receiver
   * @return shares shares of LP tokens minted
   */
  function deposit(
    uint256 assets,
    address receiver
  ) public virtual returns (uint256 shares) {
    // Check for rounding error since we round down in previewDeposit.
    require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");

    perpetualAtlanticVault.updateFunding();

    // Need to transfer before minting or ERC777s could reenter.
    collateral.transferFrom(msg.sender, address(this), assets);

    _mint(receiver, shares);

    _totalCollateral += assets;

    emit Deposit(msg.sender, receiver, assets, shares);
  }

  /**
   * @notice redeem ERC4626 token
   * @param shares shares
   * @param receiver receiver
   * @param owner owner
   * @return assets native tokens to be received
   * @return rdpxAmount rdpx tokens to be received
   */
  function redeem(
    uint256 shares,
    address receiver,
    address owner
  ) public returns (uint256 assets, uint256 rdpxAmount) {
    perpetualAtlanticVault.updateFunding();

    if (msg.sender != owner) {
      uint256 allowed = allowance[owner][msg.sender]; // Saves gas for limited approvals.

      if (allowed != type(uint256).max) {
        allowance[owner][msg.sender] = allowed - shares;
      }
    }
    (assets, rdpxAmount) = redeemPreview(shares);

    // Check for rounding error since we round down in previewRedeem.
    require(assets != 0, "ZERO_ASSETS");

    _rdpxCollateral -= rdpxAmount;

    beforeWithdraw(assets, shares);

    _burn(owner, shares);

    collateral.transfer(receiver, assets);

    IERC20WithBurn(rdpx).safeTransfer(receiver, rdpxAmount);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);
  }

  // ================================ PERP VAULT FUNCTIONS ================================ //

  /// @inheritdoc	IPerpetualAtlanticVaultLP
  function lockCollateral(uint256 amount) public onlyPerpVault {
    _activeCollateral += amount;
  }

  /// @inheritdoc	IPerpetualAtlanticVaultLP
  function unlockLiquidity(uint256 amount) public onlyPerpVault {
    _activeCollateral -= amount;
  }

  /// @inheritdoc	IPerpetualAtlanticVaultLP
  function addProceeds(uint256 proceeds) public onlyPerpVault {
    require(
      collateral.balanceOf(address(this)) >= _totalCollateral + proceeds,
      "Not enough collateral token was sent"
    );
    _totalCollateral += proceeds;
  }

  /// @inheritdoc	IPerpetualAtlanticVaultLP
  function subtractLoss(uint256 loss) public onlyPerpVault {
    require(
      collateral.balanceOf(address(this)) == _totalCollateral - loss,
      "Not enough collateral was sent out"
    );
    _totalCollateral -= loss;
  }

  /// @inheritdoc	IPerpetualAtlanticVaultLP
  function addRdpx(uint256 amount) public onlyPerpVault {
    require(
      IERC20WithBurn(rdpx).balanceOf(address(this)) >= _rdpxCollateral + amount,
      "Not enough rdpx token was sent"
    );
    _rdpxCollateral += amount;
  }

  // ================================ INTERNAL FUNCTUONS ================================ //

  function _convertToAssets(
    uint256 shares
  ) internal view virtual returns (uint256 assets, uint256 rdpxAmount) {
    uint256 supply = totalSupply;
    return
      (supply == 0)
        ? (shares, 0)
        : (
          shares.mulDivDown(totalCollateral(), supply),
          shares.mulDivDown(_rdpxCollateral, supply)
        );
  }

  function _beforeTokenTransfer(
    address from,
    address,
    uint256
  ) internal virtual {}

  // ================================ VIEWS ================================ //

  /// @notice Returns the total active collateral
  function activeCollateral() public view returns (uint256) {
    return _activeCollateral;
  }

  /// @notice Returns the total collateral
  function totalCollateral() public view returns (uint256) {
    return _totalCollateral;
  }

  /// @notice Returns the total rdpx collateral
  function rdpxCollateral() public view returns (uint256) {
    return _rdpxCollateral;
  }

  /// @notice Returns the total available collateral
  function totalAvailableCollateral() public view returns (uint256) {
    return _totalCollateral - _activeCollateral;
  }

  // ================================ PUBLIC VIEW FUNCTIONS ================================ //

  /// @notice Returns the amount of collateral and rdpx per share
  function redeemPreview(
    uint256 shares
  ) public view returns (uint256, uint256) {
    return _convertToAssets(shares);
  }

  /// @notice Returns the amount of shares recieved for a given amount of assets
  function previewDeposit(uint256 assets) public view returns (uint256) {
    return convertToShares(assets);
  }

  /// @notice Returns the amount of shares recieved for a given amount of assets
  function convertToShares(
    uint256 assets
  ) public view returns (uint256 shares) {
    uint256 supply = totalSupply;
    uint256 rdpxPriceInAlphaToken = perpetualAtlanticVault.getUnderlyingPrice();

    uint256 totalVaultCollateral = totalCollateral() +
      ((_rdpxCollateral * rdpxPriceInAlphaToken) / 1e8);
    return
      supply == 0 ? assets : assets.mulDivDown(supply, totalVaultCollateral);
  }

  function beforeWithdraw(uint256 assets, uint256 /*shares*/) internal {
    require(
      assets <= totalAvailableCollateral(),
      "Not enough available assets to satisfy withdrawal"
    );
    _totalCollateral -= assets;
  }

  // ================================ MODIFIERS ================================ //
  modifier onlyPerpVault() {
    require(
      msg.sender == address(perpetualAtlanticVault),
      "Only the perp vault can call this function"
    );
    _;
  }
}

//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

// Contracts
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { SafeMath } from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// Interfaces
import { IERC20WithBurn } from "../interfaces/IERC20WithBurn.sol";
import { IUniswapV2Router } from "../uniswap_V2/IUniswapV2Router.sol";
import { IUniswapV2Pair } from "../uniswap_V2/IUniswapV2Pair.sol";
import { IRdpxReserve } from "../reserve/IRdpxReserve.sol";
import { IRdpxEthOracle } from "../interfaces/IRdpxEthOracle.sol";
import { IRdpxV2Core } from "../core/IRdpxV2Core.sol";
import { IUniV2LiquidityAmo } from "../interfaces/IUniV2LiquidityAmo.sol";

// Libraries
import { UniswapV2Library } from "../uniswap_V2/libraries/UniswapV2Library.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

/// @title reLP contract
/// @author Dopex
/// @notice The reLP contract is a contract that ReLP's liquidity
contract ReLPContract is AccessControl {
  using SafeERC20 for IERC20WithBurn;
  using SafeMath for uint256;

  // ================================ STATE VARIABLES ================================ //
  struct Addresses {
    // token A address
    address tokenA; // rdpx
    // token B address
    address tokenB; // weth
    // pair address
    address pair;
    // rdpxV2Core address
    address rdpxV2Core;
    // tokenA reserve address
    address tokenAReserve;
    // amo address
    address amo;
    // rdpx price oracle
    address rdpxOracle;
    // AMM Factory
    address ammFactory;
    // AMM Router
    address ammRouter;
  }

  struct TokenAInfo {
    // tokenA reserves
    uint256 tokenAReserve;
    // rdpx price
    uint256 tokenAPrice;
    // tokenA LP reserves
    uint256 tokenALpReserve;
  }

  /// @notice  addresses of the contracts
  Addresses public addresses;

  /// @notice reLP factor
  uint256 public reLPFactor;

  /// @notice Precision used for prices, percentages and other calculations
  uint256 public constant DEFAULT_PRECISION = 1e8;

  /// @notice rdpxV2Core role
  bytes32 public constant RDPXV2CORE_ROLE = keccak256("RDPXV2CORE_ROLE");

  /// @notice liquidity slippage tolerance
  uint256 public liquiditySlippageTolerance = 5e5; // 0.5%

  /// @notice The slippage tolernce in swaps in 1e8 precision
  uint256 public slippageTolerance = 5e5; // 0.5%

  // ================================ CONSTRUCTOR ================================ //
  constructor() {
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(RDPXV2CORE_ROLE, msg.sender);
  }

  // ================================ ADMIN FUNCTIONS ================================ //
  /**
   * @notice Set the re-LP factor
   * @dev    Can only be called by admin
   * @param  _reLPFactor the bond discount factor
   **/
  function setreLpFactor(
    uint256 _reLPFactor
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    require(
      _reLPFactor > 0,
      "reLPContract: reLP factor must be greater than 0"
    );
    reLPFactor = _reLPFactor;

    emit LogSetReLpFactor(_reLPFactor);
  }

  /**
   * @notice Set the addresses of the contracts
   * @dev    Can only be called by admin
   * @param  _tokenA the token A address
   * @param  _tokenB the token B address
   * @param _pair the pair address
   * @param _rdpxV2Core the rdpxV2Core address
   * @param _tokenAReserve the token A reserve address
   * @param _amo the AMO address
   * @param _rdpxOracle the rdpx price oracle
   * @param _ammFactory the AMM Factory
   * @param _ammRouter the AMM Router
   **/
  function setAddresses(
    address _tokenA,
    address _tokenB,
    address _pair,
    address _rdpxV2Core,
    address _tokenAReserve,
    address _amo,
    address _rdpxOracle,
    address _ammFactory,
    address _ammRouter
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    require(
      _tokenA != address(0) &&
        _tokenB != address(0) &&
        _pair != address(0) &&
        _rdpxV2Core != address(0) &&
        _tokenAReserve != address(0) &&
        _amo != address(0) &&
        _rdpxOracle != address(0) &&
        _ammFactory != address(0) &&
        _ammRouter != address(0),
      "reLPContract: address cannot be 0"
    );
    addresses = Addresses({
      tokenA: _tokenA,
      tokenB: _tokenB,
      pair: _pair,
      rdpxV2Core: _rdpxV2Core,
      tokenAReserve: _tokenAReserve,
      amo: _amo,
      rdpxOracle: _rdpxOracle,
      ammFactory: _ammFactory,
      ammRouter: _ammRouter
    });

    IERC20WithBurn(addresses.pair).safeApprove(
      addresses.ammRouter,
      type(uint256).max
    );

    IERC20WithBurn(addresses.tokenA).safeApprove(
      addresses.ammRouter,
      type(uint256).max
    );

    IERC20WithBurn(addresses.tokenB).safeApprove(
      addresses.ammRouter,
      type(uint256).max
    );
  }

  /**
   * @notice sets the liquidity slippage tolerance
   * @dev    Can only be called by admin
   * @param  _liquiditySlippageTolerance the liquidity slippage tolerance
   */
  function setLiquiditySlippageTolerance(
    uint256 _liquiditySlippageTolerance
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    require(
      _liquiditySlippageTolerance > 0,
      "reLPContract: liquidity slippage tolerance must be greater than 0"
    );
    liquiditySlippageTolerance = _liquiditySlippageTolerance;
  }

  /**
   * @notice sets the slippage tolerance
   * @dev    Can only be called by admin
   * @param  _slippageTolerance the slippage tolerance
   */
  function setSlippageTolerance(
    uint256 _slippageTolerance
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    require(
      _slippageTolerance > 0,
      "reLPContract: slippage tolerance must be greater than 0"
    );
    slippageTolerance = _slippageTolerance;
  }

  // ================================ RdpxV2Core FUNCTIONS ================================ //

  /**
   * @dev   re-LPs the pool
   * @param _amount the amount to reLP
   **/
  function reLP(uint256 _amount) external onlyRole(RDPXV2CORE_ROLE) {
    // get the pool reserves
    (address tokenASorted, address tokenBSorted) = UniswapV2Library.sortTokens(
      addresses.tokenA,
      addresses.tokenB
    );
    (uint256 reserveA, uint256 reserveB) = UniswapV2Library.getReserves(
      addresses.ammFactory,
      tokenASorted,
      tokenBSorted
    );

    TokenAInfo memory tokenAInfo = TokenAInfo(0, 0, 0);

    // get tokenA reserves
    tokenAInfo.tokenAReserve = IRdpxReserve(addresses.tokenAReserve)
      .rdpxReserve(); // rdpx reserves

    // get rdpx price
    tokenAInfo.tokenAPrice = IRdpxEthOracle(addresses.rdpxOracle)
      .getRdpxPriceInEth();

    tokenAInfo.tokenALpReserve = addresses.tokenA == tokenASorted
      ? reserveA
      : reserveB;

    uint256 baseReLpRatio = (reLPFactor *
      Math.sqrt(tokenAInfo.tokenAReserve) *
      1e2) / (Math.sqrt(1e18)); // 1e6 precision

    uint256 tokenAToRemove = ((((_amount * 4) * 1e18) /
      tokenAInfo.tokenAReserve) *
      tokenAInfo.tokenALpReserve *
      baseReLpRatio) / (1e18 * DEFAULT_PRECISION * 1e2);

    uint256 totalLpSupply = IUniswapV2Pair(addresses.pair).totalSupply();

    uint256 lpToRemove = (tokenAToRemove * totalLpSupply) /
      tokenAInfo.tokenALpReserve;

    // transfer LP tokens from the AMO
    IERC20WithBurn(addresses.pair).transferFrom(
      addresses.amo,
      address(this),
      lpToRemove
    );

    // calculate min amounts to remove
    uint256 mintokenAAmount = tokenAToRemove -
      ((tokenAToRemove * liquiditySlippageTolerance) / 1e8);
    uint256 mintokenBAmount = ((tokenAToRemove * tokenAInfo.tokenAPrice) /
      1e8) -
      ((tokenAToRemove * tokenAInfo.tokenAPrice) * liquiditySlippageTolerance) /
      1e16;

    (, uint256 amountB) = IUniswapV2Router(addresses.ammRouter).removeLiquidity(
      addresses.tokenA,
      addresses.tokenB,
      lpToRemove,
      mintokenAAmount,
      mintokenBAmount,
      address(this),
      block.timestamp + 10
    );

    address[] memory path;
    path = new address[](2);
    path[0] = addresses.tokenB;
    path[1] = addresses.tokenA;

    // calculate min amount of tokenA to be received
    mintokenAAmount =
      (((amountB / 2) * tokenAInfo.tokenAPrice) / 1e8) -
      (((amountB / 2) * tokenAInfo.tokenAPrice * slippageTolerance) / 1e16);

    uint256 tokenAAmountOut = IUniswapV2Router(addresses.ammRouter)
      .swapExactTokensForTokens(
        amountB / 2,
        mintokenAAmount,
        path,
        address(this),
        block.timestamp + 10
      )[path.length - 1];

    (, , uint256 lp) = IUniswapV2Router(addresses.ammRouter).addLiquidity(
      addresses.tokenA,
      addresses.tokenB,
      tokenAAmountOut,
      amountB / 2,
      0,
      0,
      address(this),
      block.timestamp + 10
    );

    // transfer the lp to the amo
    IERC20WithBurn(addresses.pair).safeTransfer(addresses.amo, lp);
    IUniV2LiquidityAmo(addresses.amo).sync();

    // transfer rdpx to rdpxV2Core
    IERC20WithBurn(addresses.tokenA).safeTransfer(
      addresses.rdpxV2Core,
      IERC20WithBurn(addresses.tokenA).balanceOf(address(this))
    );
    IRdpxV2Core(addresses.rdpxV2Core).sync();
  }

  // ================================ Events FUNCTIONS ================================ //

  event LogSetReLpFactor(uint256 _reLPFactor);
}
