// SPDX-License-Identifier: GPL-3.0-only
pragma solidity >=0.8.0;

import {ERC721} from "solmate/tokens/ERC721.sol";
import {Owned} from "solmate/auth/Owned.sol";
import {Base64} from "solady/utils/Base64.sol";
import "./Tray.sol";
import "./Utils.sol";
import "../interface/Turnstile.sol";

contract Namespace is ERC721, Owned {
    /*//////////////////////////////////////////////////////////////
                                 ADDRESSES
    //////////////////////////////////////////////////////////////*/

    /// @notice Reference to the Tray NFT
    Tray public immutable tray;

    /// @notice Reference to the $NOTE TOKEN
    ERC20 public note;

    /// @notice Wallet that receives the revenue
    address private revenueAddress;

    /*//////////////////////////////////////////////////////////////
                                 STATE
    //////////////////////////////////////////////////////////////*/

    /// @notice References the tile for fusing by specifying the tray ID and the index within the tray
    struct CharacterData {
        /// @notice ID of the Tray NFT
        uint256 trayID;
        /// @notice Offset of the tile within the tray. Valid values 0..TILES_PER_TRAY - 1
        uint8 tileOffset;
        /// @notice Emoji modifier for the skin tone. Can have values of 0 (yellow) and 1 - 5 (light to dark). Only supported by some emojis
        uint8 skinToneModifier;
    }

    /// @notice Next Namespace ID to mint. We start with minting at ID 1
    uint256 public nextNamespaceIDToMint;

    /// @notice Maps names to NFT IDs
    mapping(string => uint256) public nameToToken;

    /// @notice Maps NFT IDs to (ASCII) names
    mapping(uint256 => string) public tokenToName;

    /// @notice Stores the character data of an NFT
    mapping(uint256 => Tray.TileData[]) private nftCharacters;

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/
    event NamespaceFused(address indexed fuser, uint256 indexed namespaceId, string indexed name);
    event RevenueAddressUpdated(address indexed oldRevenueAddress, address indexed newRevenueAddress);
    event NoteAddressUpdate(address indexed oldNoteAddress, address indexed newNoteAddress);

    /*//////////////////////////////////////////////////////////////
                                 ERRORS
    //////////////////////////////////////////////////////////////*/
    error CallerNotAllowedToFuse();
    error CallerNotAllowedToBurn();
    error InvalidNumberOfCharacters(uint256 numCharacters);
    error FusingDuplicateCharactersNotAllowed();
    error NameAlreadyRegistered(uint256 nftID);
    error TokenNotMinted(uint256 tokenID);
    error CannotFuseCharacterWithSkinTone();

    /// @notice Sets the reference to the tray
    /// @param _tray Address of the tray contract
    /// @param _note Address of the $NOTE token
    /// @param _revenueAddress Adress to send the revenue to
    constructor(
        address _tray,
        address _note,
        address _revenueAddress
    ) ERC721("Namespace", "NS") Owned(msg.sender) {
        tray = Tray(_tray);
        note = ERC20(_note);
        revenueAddress = _revenueAddress;
        if (block.chainid == 7700) {
            // Register CSR on Canto mainnnet
            Turnstile turnstile = Turnstile(0xEcf044C5B4b867CFda001101c617eCd347095B44);
            turnstile.register(tx.origin);
        }
    }

    /// @notice Get the token URI for the specified _id
    /// @param _id ID to query for
    function tokenURI(uint256 _id) public view override returns (string memory) {
        if (_ownerOf[_id] == address(0)) revert TokenNotMinted(_id);
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "',
                        tokenToName[_id],
                        '", "image": "data:image/svg+xml;base64,',
                        Base64.encode(bytes(Utils.generateSVG(nftCharacters[_id], false))),
                        '"}'
                    )
                )
            )
        );
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    /// @notice Fuse a new Namespace NFT with the referenced tiles
    /// @param _characterList The tiles to use for the fusing
    function fuse(CharacterData[] calldata _characterList) external {
        uint256 numCharacters = _characterList.length;
        if (numCharacters > 13 || numCharacters == 0) revert InvalidNumberOfCharacters(numCharacters);
        uint256 fusingCosts = 2**(13 - numCharacters) * 1e18;
        SafeTransferLib.safeTransferFrom(note, msg.sender, revenueAddress, fusingCosts);
        uint256 namespaceIDToMint = ++nextNamespaceIDToMint;
        Tray.TileData[] storage nftToMintCharacters = nftCharacters[namespaceIDToMint];
        bytes memory bName = new bytes(numCharacters * 33); // Used to convert into a string. Can be 33 times longer than the string at most (longest zalgo characters is 33 bytes)
        uint256 numBytes;
        // Extract unique trays for burning them later on
        uint256 numUniqueTrays;
        uint256[] memory uniqueTrays = new uint256[](_characterList.length);
        for (uint256 i; i < numCharacters; ++i) {
            bool isLastTrayEntry = true;
            uint256 trayID = _characterList[i].trayID;
            uint8 tileOffset = _characterList[i].tileOffset;
            // Check for duplicate characters in the provided list. 1/2 * n^2 loop iterations, but n is bounded to 13 and we do not perform any storage operations
            for (uint256 j = i + 1; j < numCharacters; ++j) {
                if (_characterList[j].trayID == trayID) {
                    isLastTrayEntry = false;
                    if (_characterList[j].tileOffset == tileOffset) revert FusingDuplicateCharactersNotAllowed();
                }
            }
            Tray.TileData memory tileData = tray.getTile(trayID, tileOffset); // Will revert if tileOffset is too high
            uint8 characterModifier = tileData.characterModifier;

            if (tileData.fontClass != 0 && _characterList[i].skinToneModifier != 0) {
                revert CannotFuseCharacterWithSkinTone();
            }

            if (tileData.fontClass == 0) {
                // Emoji
                characterModifier = _characterList[i].skinToneModifier;
            }
            bytes memory charAsBytes = Utils.characterToUnicodeBytes(0, tileData.characterIndex, characterModifier);
            tileData.characterModifier = characterModifier;
            uint256 numBytesChar = charAsBytes.length;
            for (uint256 j; j < numBytesChar; ++j) {
                bName[numBytes + j] = charAsBytes[j];
            }
            numBytes += numBytesChar;
            nftToMintCharacters.push(tileData);
            // We keep track of the unique trays NFTs (for burning them) and only check the owner once for the last occurence of the tray
            if (isLastTrayEntry) {
                uniqueTrays[numUniqueTrays++] = trayID;
                // Verify address is allowed to fuse
                address trayOwner = tray.ownerOf(trayID);
                if (
                    trayOwner != msg.sender &&
                    tray.getApproved(trayID) != msg.sender &&
                    !tray.isApprovedForAll(trayOwner, msg.sender)
                ) revert CallerNotAllowedToFuse();
            }
        }
        // Set array to the real length (in bytes) to avoid zero bytes in the end when doing the string conversion
        assembly {
            mstore(bName, numBytes)
        }
        string memory nameToRegister = string(bName);
        uint256 currentRegisteredID = nameToToken[nameToRegister];
        if (currentRegisteredID != 0) revert NameAlreadyRegistered(currentRegisteredID);
        nameToToken[nameToRegister] = namespaceIDToMint;
        tokenToName[namespaceIDToMint] = nameToRegister;

        for (uint256 i; i < numUniqueTrays; ++i) {
            tray.burn(uniqueTrays[i]);
        }
        _mint(msg.sender, namespaceIDToMint);
        // Although _mint already emits an event, we additionally emit one because of the name
        emit NamespaceFused(msg.sender, namespaceIDToMint, nameToRegister);
    }

    /// @notice Burn a specified Namespace NFT
    /// @param _id Namespace NFT ID
    function burn(uint256 _id) external {
        address nftOwner = ownerOf(_id);
        if (nftOwner != msg.sender && getApproved[_id] != msg.sender && !isApprovedForAll[nftOwner][msg.sender])
            revert CallerNotAllowedToBurn();
        string memory associatedName = tokenToName[_id];
        delete tokenToName[_id];
        delete nameToToken[associatedName];
        _burn(_id);
    }

    /// @notice Change the address of the $NOTE token
    /// @param _newNoteAddress New address to use
    function changeNoteAddress(address _newNoteAddress) external onlyOwner {
        address currentNoteAddress = address(note);
        note = ERC20(_newNoteAddress);
        emit NoteAddressUpdate(currentNoteAddress, _newNoteAddress);
    }

    /// @notice Change the revenue address
    /// @param _newRevenueAddress New address to use
    function changeRevenueAddress(address _newRevenueAddress) external onlyOwner {
        address currentRevenueAddress = revenueAddress;
        revenueAddress = _newRevenueAddress;
        emit RevenueAddressUpdated(currentRevenueAddress, _newRevenueAddress);
    }
}

// SPDX-License-Identifier: GPL-3.0-only
pragma solidity >=0.8.0;

import "./Tray.sol";
import {LibString} from "solmate/utils/LibString.sol";

/// @notice Utiltities for the on-chain SVG generation of the text data and pseudo randomness
library Utils {
    /*//////////////////////////////////////////////////////////////
                                 ERRORS
    //////////////////////////////////////////////////////////////*/
    error EmojiDoesNotSupportSkinToneModifier(uint16 emojiIndex);
    error InvalidSkinToneModifierProvided(uint256 characterModifier);

    /// @notice UTF-8 encoding of the squiggle characters. The characters have a different length (1 to 3 bytes)
    bytes private constant FONT_SQUIGGLE =
        hex"CEB1E182A6C688D483D2BDCF9DC9A0D48BCEB9CA9DC699CA85C9B1C9B3CF83CF81CF99C9BECA82C69ACF85CA8BC9AF78E183A7C8A5";

    /// @notice UTF-8 encoding of possible characters that can be above a letter for Zalgo. All characters have a length of 2 bytes
    bytes private constant ZALGO_ABOVE_LETTER =
        hex"CC80CC81CC82CC83CC84CC85CC86CC87CC88CC89CC8ACC8BCC8CCC8DCC8ECC8FCC90CC91CC92CC93CC94CC95CC9ACC9BCCBDCCBECCBFCD80CD81CD82CD83CD84CD86CD8ACD8BCD8CCD90CD91CD92CD97CD98CD9BCD9DCD9ECDA0CDA1";

    /// @notice Number of characters that can be above a letter for Zalgo
    uint256 private constant ZALGO_NUM_ABOVE = 46;

    /// @notice UTF-8 encoding of possible characters that can be below a letter for Zalgo. All characters have a length of 2 bytes
    bytes private constant ZALGO_BELOW_LETTER =
        hex"CC96CC97CC98CC99CC9CCC9DCC9ECC9FCCA0CCA1CCA2CCA3CCA4CCA5CCA6CCA7CCA8CCA9CCAACCABCCACCCADCCAECCAFCCB0CCB1CCB2CCB3CCB9CCBACCBBCCBCCD85CD87CD88CD89CD8DCD8ECD93CD94CD95CD96CD99CD9ACD9CCD9FCDA2";

    /// @notice Number of characters that can be below a letter for Zalgo
    uint256 private constant ZALGO_NUM_BELOW = 47;

    /// @notice UTF-8 encoding of possible characters that can be over a letter (i.e., in the middle) for Zalgo. All characters have a length of 2 bytes
    bytes private constant ZALGO_OVER_LETTER = hex"CCB4CCB5CCB6CCB7CCB8";

    /// @notice Number of characters that can be over a letter for Zalgo
    uint256 private constant ZALGO_NUM_OVER = 5;

    /// @notice UTF-8 encoding of all supported emojis. They are sorted by their length in bytes (all with 3 bytes first, then all with four bytes, ...)
    /// For emojis with the same length, the emojis that do not support skin tone modifier come first.
    /// @dev This string is generated by the script utils/gen_emoji_string.py
    bytes private constant EMOJIS =
        hex"";

    /// @notice These constants are used to efficiently parse the UTF-8 encoding of the emojis. They enable constant time lookup of any character.
    /// This is done by storing the byte offset where each group (with the same length) starts and by storing the amount per group.
    /// Furthermore, we store the number of emojis where the skin tone can be modified such that we can revert when this is not supported (and would result in wrong characters).

    /// @notice LE refers to "less or equal than", i.e. we store the amount of emojis that are less or equal than 3, 4, 6, 7, 8 bytes
    uint256 private constant EMOJIS_LE_THREE_BYTES = 17;
    uint256 private constant EMOJIS_LE_FOUR_BYTES = 383;
    uint256 private constant EMOJIS_LE_SIX_BYTES = 409;
    uint256 private constant EMOJIS_LE_SEVEN_BYTES = 416;
    uint256 private constant EMOJIS_LE_EIGHT_BYTES = 419;
    // uint256 constant EMOJIS_LE_FOURTEEN_BYTES = 420;

    /// @notice Number of emojis where the skin tone can be modified for three and four bytes
    uint256 private constant EMOJIS_MOD_SKIN_TONE_THREE_BYTES = 2;
    uint256 private constant EMOJIS_MOD_SKIN_TONE_FOUR_BYTES = 47;
    // 0 for 7, 8, 14 bytes

    /// @notice Byte offset where the groups start
    uint256 private constant EMOJIS_BYTE_OFFSET_FOUR_BYTES = 51; // 17 * 3
    uint256 private constant EMOJIS_BYTE_OFFSET_SIX_BYTES = 1515; // 17 * 3 + 366 * 4
    uint256 private constant EMOJIS_BYTE_OFFSET_SEVEN_BYTES = 1671; // 17 * 3 + 366 * 4 + 26 * 6
    uint256 private constant EMOJIS_BYTE_OFFSET_EIGHT_BYTES = 1720; // 17 * 3 + 366 * 4 + 26 * 6 + 7 * 7
    uint256 private constant EMOJIS_BYTE_OFFSET_FOURTEEN_BYTES = 1744; // 17 * 3 + 366 * 4 + 26 * 6 + 7 * 7 + 3 * 8

    /// @notice Convert a given font class, character index, and a seed (for font classes with randomness) to their Unicode representation as bytes
    /// @param _fontClass The class to convert
    /// @param _characterIndex Index within the class
    /// @param _characterModifier Some characters have numeric modifiers (skin tone modifier for emojis, seed generated at minting for zalgo)
    function characterToUnicodeBytes(
        uint8 _fontClass,
        uint16 _characterIndex,
        uint256 _characterModifier
    ) internal pure returns (bytes memory) {
        if (_fontClass == 0) {
            // Emojis
            uint256 byteOffset;
            uint256 numBytes;
            bool supportsSkinToneModifier;
            if (_characterIndex < EMOJIS_LE_THREE_BYTES) {
                numBytes = 3;
                byteOffset = _characterIndex * 3;
                supportsSkinToneModifier = _characterIndex >= EMOJIS_LE_THREE_BYTES - EMOJIS_MOD_SKIN_TONE_THREE_BYTES;
            } else if (_characterIndex < EMOJIS_LE_FOUR_BYTES) {
                numBytes = 4;
                byteOffset = EMOJIS_BYTE_OFFSET_FOUR_BYTES + (_characterIndex - EMOJIS_LE_THREE_BYTES) * 4;
                supportsSkinToneModifier = _characterIndex >= EMOJIS_LE_FOUR_BYTES - EMOJIS_MOD_SKIN_TONE_FOUR_BYTES;
            } else if (_characterIndex < EMOJIS_LE_SIX_BYTES) {
                numBytes = 6;
                byteOffset = EMOJIS_BYTE_OFFSET_SIX_BYTES + (_characterIndex - EMOJIS_LE_FOUR_BYTES) * 6;
            } else if (_characterIndex < EMOJIS_LE_SEVEN_BYTES) {
                numBytes = 7;
                byteOffset = EMOJIS_BYTE_OFFSET_SEVEN_BYTES + (_characterIndex - EMOJIS_LE_SIX_BYTES) * 7;
            } else if (_characterIndex < EMOJIS_LE_EIGHT_BYTES) {
                numBytes = 8;
                byteOffset = EMOJIS_BYTE_OFFSET_EIGHT_BYTES + (_characterIndex - EMOJIS_LE_SEVEN_BYTES) * 8;
            } else {
                numBytes = 14;
                byteOffset = EMOJIS_BYTE_OFFSET_FOURTEEN_BYTES + (_characterIndex - EMOJIS_LE_EIGHT_BYTES) * 14;
            }
            bytes memory character = abi.encodePacked(
                EMOJIS[byteOffset],
                EMOJIS[byteOffset + 1],
                EMOJIS[byteOffset + 2]
            );
            for (uint256 i = 3; i < numBytes; ++i) {
                character = abi.encodePacked(character, EMOJIS[byteOffset + i]);
            }
            if (_characterModifier != 0) {
                if (!supportsSkinToneModifier) revert EmojiDoesNotSupportSkinToneModifier(_characterIndex);
                if (_characterModifier == 1) {
                    character = abi.encodePacked(character, hex"F09F8FBB");
                } else if (_characterModifier == 2) {
                    character = abi.encodePacked(character, hex"F09F8FBC");
                } else if (_characterModifier == 3) {
                    character = abi.encodePacked(character, hex"F09F8FBD");
                } else if (_characterModifier == 4) {
                    character = abi.encodePacked(character, hex"F09F8FBE");
                } else if (_characterModifier == 5) {
                    character = abi.encodePacked(character, hex"F09F8FBF");
                } else {
                    revert InvalidSkinToneModifierProvided(_characterModifier);
                }
            }
            return character;
        } else if (_fontClass == 1) {
            // Basic, sans-serif text
            uint8 asciiStartingIndex = 97; // Starting index for (lowercase) characters
            if (_characterIndex > 25) {
                asciiStartingIndex = 22; // Starting index for (lowercase) characters - 25
            }
            return abi.encodePacked(bytes1(asciiStartingIndex + uint8(_characterIndex)));
        } else if (_fontClass == 7) {
            // Zalgo
            uint8 asciiStartingIndex = 97;
            uint256 numAbove = (_characterModifier % 7) + 1;
            // We do not reuse the same seed for the following generations to avoid any symmetries, e.g. that 2 chars above would also always result in 2 chars below
            _characterModifier = iteratePRNG(_characterModifier);
            uint256 numMiddle = _characterModifier % 2;
            _characterModifier = iteratePRNG(_characterModifier);
            uint256 numBelow = (_characterModifier % 7) + 1;
            bytes memory character = abi.encodePacked(bytes1(asciiStartingIndex + uint8(_characterIndex)));
            for (uint256 i; i < numAbove; ++i) {
                _characterModifier = iteratePRNG(_characterModifier);
                uint256 characterIndex = (_characterModifier % ZALGO_NUM_ABOVE) * 2;
                character = abi.encodePacked(
                    character,
                    ZALGO_ABOVE_LETTER[characterIndex],
                    ZALGO_ABOVE_LETTER[characterIndex + 1]
                );
            }
            for (uint256 i; i < numMiddle; ++i) {
                _characterModifier = iteratePRNG(_characterModifier);
                uint256 characterIndex = (_characterModifier % ZALGO_NUM_OVER) * 2;
                character = abi.encodePacked(
                    character,
                    ZALGO_OVER_LETTER[characterIndex],
                    ZALGO_OVER_LETTER[characterIndex + 1]
                );
            }
            for (uint256 i; i < numBelow; ++i) {
                _characterModifier = iteratePRNG(_characterModifier);
                uint256 characterIndex = (_characterModifier % ZALGO_NUM_BELOW) * 2;
                character = abi.encodePacked(
                    character,
                    ZALGO_BELOW_LETTER[characterIndex],
                    ZALGO_BELOW_LETTER[characterIndex + 1]
                );
            }
            return character;
        } else {
            bytes memory startingSequence;
            if (_fontClass == 2) {
                // Script
                // Those 3 characters come from a different UTF-8 block, we therefore hardcode them
                if (_characterIndex == 4) return hex"F09D9192";
                if (_characterIndex == 6) return hex"F09D9194";
                if (_characterIndex == 14) return hex"F09D919C";
                startingSequence = hex"F09D92B6";
            } else if (_fontClass == 3) {
                // Script Bold
                startingSequence = hex"F09D93AA";
            } else if (_fontClass == 4) {
                // Olde
                startingSequence = hex"F09D949E";
            } else if (_fontClass == 5) {
                // Olde Bold
                startingSequence = hex"F09D9686";
            } else if (_fontClass == 6) {
                // Squiggle
                // Font: αႦƈԃҽϝɠԋιʝƙʅɱɳσρϙɾʂƚυʋɯxყȥ
                // Hex encoding: CEB1 E182A6 C688 D483 D2BD CF9D C9A0 D48B CEB9 CA9D C699 CA85 C9B1 C9B3 CF83 CF81 CF99 C9BE CA82 C69A CF85 CA8B C9AF 78 E183A7 C8A5
                if (_characterIndex == 0) {
                    return abi.encodePacked(FONT_SQUIGGLE[0], FONT_SQUIGGLE[1]);
                } else if (_characterIndex == 1) {
                    return abi.encodePacked(FONT_SQUIGGLE[2], FONT_SQUIGGLE[3], FONT_SQUIGGLE[4]);
                } else if (_characterIndex < 23 || _characterIndex == 25) {
                    uint256 offset = (_characterIndex - 2) * 2;
                    return abi.encodePacked(FONT_SQUIGGLE[5 + offset], FONT_SQUIGGLE[6 + offset]);
                } else if (_characterIndex == 23) {
                    return abi.encodePacked(FONT_SQUIGGLE[47]);
                } else if (_characterIndex == 24) {
                    return abi.encodePacked(FONT_SQUIGGLE[48], FONT_SQUIGGLE[49], FONT_SQUIGGLE[50]);
                }
            } else if (_fontClass == 8) {
                // Blocks
                startingSequence = hex"F09F84B0";
            } else if (_fontClass == 9) {
                // Blocks inverted
                startingSequence = hex"F09F85B0";
            }
            return _getUtfSequence(startingSequence, uint8(_characterIndex));
        }
    }

    /// @notice Generate the SVG for the given tiles
    /// @param _tiles Tiles to generate the SVG for
    /// @param _isTray If true, a border will be added around the tiles
    function generateSVG(Tray.TileData[] memory _tiles, bool _isTray) internal pure returns (string memory) {
        string memory innerData;
        uint256 dx = 400 / (_tiles.length + 1);
        for (uint256 i; i < _tiles.length; ++i) {
            innerData = string.concat(
                innerData,
                '<text dominant-baseline="middle" text-anchor="middle" y="100" x="',
                LibString.toString(dx * (i + 1)),
                '">',
                string(
                    characterToUnicodeBytes(_tiles[i].fontClass, _tiles[i].characterIndex, _tiles[i].characterModifier)
                ),
                "</text>"
            );
            if (_isTray) {
                innerData = string.concat(
                    innerData,
                    '<rect width="34" height="60" y="70" x="',
                    LibString.toString(dx * (i + 1) - 17),
                    '" stroke="black" stroke-width="1" fill="none"></rect>'
                );
            }
        }
        return
            string.concat(
                '<svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMinYMin meet" viewBox="0 0 400 200"><style>text { font-family: sans-serif; font-size: 30px; }</style>',
                innerData,
                "</svg>"
            );
    }

    /// @notice Simple PRNG to generate new numbers based on the current state
