        } else {
            lastByteVal = 128 + (lastByteSum % 192);
            _startingSequence[2] = bytes1(uint8(_startingSequence[2]) + 1);
            _startingSequence[3] = bytes1(lastByteVal);
        }
        return _startingSequence;
    }
