// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ISale} from "../interfaces/ISale.sol";
import {ISaleFactory} from "../interfaces/ISaleFactory.sol";
import {IEscher721} from "../interfaces/IEscher721.sol";
import {Initializable} from "openzeppelin-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "openzeppelin-upgradeable/access/OwnableUpgradeable.sol";

contract FixedPrice is Initializable, OwnableUpgradeable, ISale {
    address public immutable factory;
    /// store nextId and remainingSupply, where nextId increases and remainingSupply decreases to 0
    /// avoids strict equality of current == final
    struct Sale {
        // slot 1
        uint48 currentId;
        uint48 finalId;
        address edition;
        // slot 2
        uint96 price;
        address payable saleReceiver;
        // slot 3
        uint96 startTime;
    }

    /// @notice sale info for this fixed price edition
    Sale public sale;

    /// @notice Event emitted when sale created
    /// @param _saleInfo the sale info for the edition
    event Start(Sale _saleInfo);
    /// @notice Event emitted when sale ends
    /// @param _saleInfo the sale info for the edition
    event End(Sale _saleInfo);
    /// @notice Event emitted when a user buys an edition
    /// @param _buyer the address of the buyer
    /// @param _amount the amount of editions bought
    /// @param _value the total value of the purchase
    /// @param _saleInfo the sale info for the edition
    event Buy(address indexed _buyer, uint256 _amount, uint256 _value, Sale _saleInfo);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        factory = msg.sender;
        _disableInitializers();
        sale = Sale(0, 0, address(0), type(uint96).max, payable(0), type(uint96).max);
    }

    /// @notice Owner can cancel current sale
    function cancel() external onlyOwner {
        require(block.timestamp < sale.startTime, "TOO LATE");
        _end(sale);
    }

    /// @notice buy from a fixed price sale after the sale starts
    /// @param _amount the amount of editions to buy
    function buy(uint256 _amount) external payable {
        Sale memory sale_ = sale;
        IEscher721 nft = IEscher721(sale_.edition);
        require(block.timestamp >= sale_.startTime, "TOO SOON");
        require(_amount * sale_.price == msg.value, "WRONG PRICE");
        uint48 newId = uint48(_amount) + sale_.currentId;
        require(newId <= sale_.finalId, "TOO MANY");

        for (uint48 x = sale_.currentId + 1; x <= newId; x++) {
            nft.mint(msg.sender, x);
        }

        sale.currentId = newId;

        emit Buy(msg.sender, _amount, msg.value, sale);

        if (newId == sale_.finalId) _end(sale);
    }

    /// @notice initialize the proxy sale contract
    /// @param _sale the sale info
    function initialize(Sale memory _sale) public initializer {
        sale = _sale;
        _transferOwnership(_sale.saleReceiver);
        emit Start(_sale);
    }

    /// @notice cancel a fixed price sale
    /// @notice the price of the sale
    function getPrice() public view returns (uint256) {
        return sale.price;
    }

    /// @notice the start time of the sale
    function startTime() public view returns (uint256) {
        return sale.startTime;
    }

    /// @notice the edition contract having the sale
    function editionContract() public view returns (address) {
        return sale.edition;
    }

    /// @notice the number of NFTs still available
    function available() public view returns (uint256) {
        return sale.finalId - sale.currentId;
    }

    /// @notice Owner can cancel current sale
    /// @param _sale the sale info
    function _end(Sale memory _sale) internal {
        emit End(_sale);
        ISaleFactory(factory).feeReceiver().transfer(address(this).balance / 20);
        selfdestruct(_sale.saleReceiver);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ISale} from "../interfaces/ISale.sol";
import {ISaleFactory} from "../interfaces/ISaleFactory.sol";
import {IEscher721} from "../interfaces/IEscher721.sol";
import {Initializable} from "openzeppelin-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "openzeppelin-upgradeable/access/OwnableUpgradeable.sol";

contract LPDA is Initializable, OwnableUpgradeable, ISale {
    address public immutable factory;
    /// we use different uints and some weird ordering to pack variables into 32 bytes
    struct Sale {
        // slot 1
        uint48 currentId;
        uint48 finalId;
        address edition;
        // slot 2
        uint80 startPrice;
        uint80 finalPrice;
        uint80 dropPerSecond;
        // slot 3
        uint96 endTime;
        address payable saleReceiver;
        // slot 4
        uint96 startTime;
    }

    struct Receipt {
        uint48 amount;
        uint80 balance;
    }

    /// @notice sale info for this fixed price edition
    Sale public sale;
    uint48 public amountSold = 0;

    /// @notice tracked the amount paid by people during the LPDA
    mapping(address => Receipt) public receipts;

    event Start(Sale _saleInfo);
    event End(Sale _saleInfo);
    event Buy(address indexed _buyer, uint256 _amount, uint256 _value, Sale _saleInfo);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        factory = msg.sender;
        _disableInitializers();
        uint48 x = type(uint48).max;
        uint80 y = type(uint80).max;
        uint96 z = type(uint96).max;
        address payable i = payable(address(0));
        sale = LPDA.Sale(x, x, i, y, y, y, z, i, z);
    }

    /// @notice buy from a fixed price sale after the sale starts
    /// @param _amount the amount of editions to buy
    function buy(uint256 _amount) external payable {
        uint48 amount = uint48(_amount);
        Sale memory temp = sale;
        IEscher721 nft = IEscher721(temp.edition);
        require(block.timestamp >= temp.startTime, "TOO SOON");
        uint256 price = getPrice();
        require(msg.value >= amount * price, "WRONG PRICE");

        amountSold += amount;
        uint48 newId = amount + temp.currentId;
        require(newId <= temp.finalId, "TOO MANY");

        receipts[msg.sender].amount += amount;
        receipts[msg.sender].balance += uint80(msg.value);

        for (uint256 x = temp.currentId + 1; x <= newId; x++) {
            nft.mint(msg.sender, x);
        }

        sale.currentId = newId;

        emit Buy(msg.sender, amount, msg.value, temp);

        if (newId == temp.finalId) {
            sale.finalPrice = uint80(price);
            uint256 totalSale = price * amountSold;
            uint256 fee = totalSale / 20;
            ISaleFactory(factory).feeReceiver().transfer(fee);
            temp.saleReceiver.transfer(totalSale - fee);
            _end();
        }
    }

    /// @notice cancel a fixed price sale
    function cancel() external onlyOwner {
        require(block.timestamp < sale.startTime, "TOO LATE");
        sale.finalId = sale.currentId;
        _end();
    }

    /// @notice allow a buyer to get a refund on the current price difference
    function refund() public {
        Receipt memory r = receipts[msg.sender];
        uint80 price = uint80(getPrice()) * r.amount;
        uint80 owed = r.balance - price;
        require(owed > 0, "NOTHING TO REFUND");
        receipts[msg.sender].balance = price;
        payable(msg.sender).transfer(owed);
    }

    /// @notice initialize the proxy sale contract
    /// @param _sale the sale info
    function initialize(Sale calldata _sale) public initializer {
        sale = _sale;
        _transferOwnership(sale.saleReceiver);
        emit Start(_sale);
    }

    /// @notice the price of the sale
    function getPrice() public view returns (uint256) {
        Sale memory temp = sale;
        (uint256 start, uint256 end) = (temp.startTime, temp.endTime);
        if (block.timestamp < start) return type(uint256).max;
        if (temp.currentId == temp.finalId) return temp.finalPrice;

        uint256 timeElapsed = end > block.timestamp ? block.timestamp - start : end - start;
        return temp.startPrice - (temp.dropPerSecond * timeElapsed);
    }

    /// @notice the start time of the sale
    function startTime() public view returns (uint256) {
        return sale.startTime;
    }

    /// @notice the edition contract having the sale
    function editionContract() public view returns (address) {
        return sale.edition;
    }

    /// @notice the number of NFTs still available
    function available() public view returns (uint256) {
        return sale.finalId - sale.currentId;
    }

    function lowestPrice() public view returns (uint256) {
        return sale.startPrice - (sale.dropPerSecond * (sale.endTime - sale.startTime));
    }

    function _end() internal {
        emit End(sale);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ISale} from "../interfaces/ISale.sol";
import {ISaleFactory} from "../interfaces/ISaleFactory.sol";
import {IEscher721} from "../interfaces/IEscher721.sol";
import {Initializable} from "openzeppelin-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "openzeppelin-upgradeable/access/OwnableUpgradeable.sol";

contract OpenEdition is Initializable, OwnableUpgradeable, ISale {
    address public immutable factory;
    /// we use different uints and some weird ordering to pack variables into 32 bytes
    struct Sale {
        // slot 1
        uint72 price;
        uint24 currentId;
        address edition;
        // slot 2
        uint96 startTime;
        address payable saleReceiver;
        // slot 3
        uint96 endTime;
    }

    /// @notice sale info for this fixed price edition
    Sale public sale;

    /// @notice Event emitted when sale created
    /// @param _saleInfo the sale info for the edition
    event Start(Sale _saleInfo);
    /// @notice Event emitted when sale ends
    /// @param _saleInfo the sale info for the edition
    event End(Sale _saleInfo);
    /// @notice Event emitted when a user buys an edition
    /// @param _buyer the address of the buyer
    /// @param _amount the amount of editions bought
    /// @param _value the total value of the purchase
    /// @param _saleInfo the sale info for the edition
    event Buy(address indexed _buyer, uint256 _amount, uint256 _value, Sale _saleInfo);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        factory = msg.sender;
        _disableInitializers();
        sale = Sale(
            type(uint72).max,
            type(uint24).max,
            address(0),
            type(uint96).max,
            payable(0),
            type(uint96).max
        );
    }

    /// @notice buy from a fixed price sale after the sale starts
    /// @param _amount the amount of editions to buy
    function buy(uint256 _amount) external payable {
        uint24 amount = uint24(_amount);
        Sale memory temp = sale;
        IEscher721 nft = IEscher721(temp.edition);
        require(block.timestamp >= temp.startTime, "TOO SOON");
        require(block.timestamp < temp.endTime, "TOO LATE");
        require(amount * sale.price == msg.value, "WRONG PRICE");
        uint24 newId = amount + temp.currentId;

        for (uint24 x = temp.currentId + 1; x <= newId; x++) {
            nft.mint(msg.sender, x);
        }
        sale.currentId = newId;

        emit Buy(msg.sender, amount, msg.value, temp);
    }

    /// @notice cancel a fixed price sale
    function cancel() external onlyOwner {
        require(block.timestamp < sale.startTime, "TOO LATE");
        _end(sale);
    }

    /// @notice initialize the proxy sale contract
    /// @param _sale the sale info
    function initialize(Sale memory _sale) public initializer {
        sale = _sale;
        _transferOwnership(sale.saleReceiver);
        emit Start(_sale);
    }

    /// @notice finish an open edition and payout the artist
    function finalize() public {
        Sale memory temp = sale;
        require(block.timestamp >= temp.endTime, "TOO SOON");
        ISaleFactory(factory).feeReceiver().transfer(address(this).balance / 20);
        _end(temp);
    }

    /// @notice the price of the sale
    function getPrice() public view returns (uint256) {
        return sale.price;
    }

    /// @notice the start time of the sale
    function startTime() public view returns (uint256) {
        return sale.startTime;
    }

    /// @notice how much time is left in the sale
    function timeLeft() public view returns (uint256) {
        return sale.endTime > block.timestamp ? sale.endTime - block.timestamp : 0;
    }

    /// @notice the edition contract having the sale
    function editionContract() public view returns (address) {
        return sale.edition;
    }

    function available() public view returns (uint256) {
        return sale.endTime > block.timestamp ? type(uint24).max : 0;
    }

    function _end(Sale memory _sale) internal {
        emit End(_sale);
        selfdestruct(_sale.saleReceiver);
    }
}
